import React from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { Route, Switch, Link, Redirect, useLocation } from 'react-router-dom';

function LoginForm(props){
    return(
        <Form>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label><h5>Email Address</h5></Form.Label>
                <Form.Control type="email" placeholder="Enter email" />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label><h5>Password</h5></Form.Label>
                <Form.Control type="password" placeholder="Enter password" />
            </Form.Group>
            <Button variant="primary" type="submit" className="rounded-pill login-button" size="sm">
                <p>{props.buttonText}</p>
            </Button>
            <Link to="./RegisterScreen.js"><p>Don't have an account? Register here!</p></Link>
        </Form>
    );
}

export default LoginForm;
