import React, { useEffect } from 'react';
import axios from "axios";
import LoginScreen from "./LoginScreen.jsx";
import Sentiment from './Sentiment';
import Header from './Header'
import useToken from './useToken'
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";

function App() {
  const { token, setToken } = useToken();

  useEffect(() => {
    // Check if the token is set
    if (token) {
      // If the token is set, trigger the redirection
      navigate('/Sentiment');
    }
  }, [token]); // Watch for changes in the "token" state

  const navigate = useNavigate(); // Initialize navigate

  const handleLogin = async (email, password) => {
    try {
      const response = await axios.post('/token', { email, password });
      const accessToken = response.data.access_token;

      // Set the token
      setToken(accessToken);
    } catch (error) {
      // Handle login error
      console.error(error);
    }
  };

  return (
    <div className="App">
      <Router>
        <div className="App">
          <Header token={removeToken} />

          <Routes>
            <Route
              exact
              path="/Sentiment"
              element={<Sentiment token={token} setToken={setToken} />}
            />
          </Routes>

          {!token && (
            <LoginScreen
              setToken={handleLogin}
            />
          )}
        </div>
      </Router>
    </div>
  );
}

export default App;
