import React, { useEffect } from 'react';
import axios from 'axios';
import LoginScreen from './LoginScreen.jsx';
import Sentiment from './Sentiment';
import Header from './Header';
import useToken from './useToken'; // Import removeToken
import removeToken from './useToken.js';
import {
  BrowserRouter as Router,
  Navigate, 
  Routes,
  Route,
  useNavigate, // Import useNavigate
} from 'react-router-dom';


function App() {
  const { token, setToken } = useToken();

  useEffect(() => {
    // Check if the token is set
    if (token) {
      // If the token is set, trigger the redirection
      return <Navigate to="/Sentiment" />;
    }
  }, [token]); // Watch for changes in the "token" state

  const handleLogin = async (email, password) => {
    try {
      const response = await axios.post('/token', { email, password });
      const accessToken = response.data.access_token;

      // Set the token
      setToken(accessToken);
    } catch (error) {
      // Handle login error
      console.error(error);
    }
  };

  return (
    <div className="App">
      <Router>
        <div className="App">
          <Header token={removeToken} />

          <Routes>
            <Route
              exact
              path="/Sentiment"
              element={<Sentiment token={token} setToken={setToken} />}
            />
            <Route
              path="/LoginScreen"
              element={
                <LoginScreen
                  setToken={(email, password) => handleLogin(email, password)}
                />
              }
            />
          </Routes>
        </div>
      </Router>
    </div>
  );
}

export default App;
