import React from 'react';
import axios from "axios";
import { useHistory } from 'react-router-dom';

function Header(props) {
  const history = useHistory();

  function logMeOut() {
    axios({
      method: "POST",
      url:"/logout",
    })
    .then((response) => {
       props.token(null); // Assuming calling this function with null removes the token
       history.push('/signin'); // Redirect to sign-in page
    }).catch((error) => {
      if (error.response) {
        console.log(error.response)
        console.log(error.response.status)
        console.log(error.response.headers)
      }
    });
  }

  function logMeIn() {
    // Placeholder for login functionality
    console.log("Login functionality goes here.");
  }

  return (
    <header className="App-header bg-primary text-white d-flex justify-content-end p-3">
      {props.token ? (
        <button onClick={logMeOut} className="btn btn-secondary">Logout</button>
      ) : (
        <button onClick={logMeIn} className="btn btn-secondary">Login</button>
      )}
    </header>
  );
}

export default Header;
