import React from 'react';
import axios from "axios";
import { useNavigate } from 'react-router-dom';
import useToken from './useToken.js';


function Header(props) {
  const { token, removeToken, setToken } = useToken();
  const navigate = useNavigate();

  function logMeOut() {
    axios({
      method: "POST",
      url: "/logout",
    })
    .then((response) => {
       props.token(null); // Update according to your token handling logic
       navigate('/'); // Redirect to sign-in page
    }).catch((error) => {
      if (error.response) {
        console.log(error.response);
        console.log(error.response.status);
        console.log(error.response.headers);
      }
    });
  }

  function logMeIn() {
    navigate('/'); // Redirect to sign-in page
  }

  return (
    <header className="App-header d-flex justify-content-end p-3" style={{ 
      position: 'absolute', 
      top: 0, 
      right: 0, 
      backgroundColor: 'transparent', 
      color: 'rgba(255, 255, 255, 0.8)',
      zIndex: 10000, // Increased z-index
      width: '100%', // Ensuring it spans the entire width
      pointerEvents: 'all' // Explicitly allow pointer events
    }}>
      {!token && token!=="" &&token!== undefined ? (
        <span className="cursor-pointer" onClick={logMeIn} style={{ pointerEvents: 'all' }}>Login</span>
      ) : (
        <span className="cursor-pointer" onClick={logMeIn} style={{ pointerEvents: 'all' }}>Login</span>
      )}
    </header>
  );
}

export default Header;
