import React from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { Link } from "react-router-dom";

function LoginForm(props){

    const [loginForm, setloginForm] = useState({
        email: "",
        password: ""
      })


    const handleEmailChange = (event) => {
        setEmail(event.target.value);
    };

    const handlePasswordChange = (event) => {
        setPassword(event.target.value);
    };

    const logMeIn = (event: React.SyntheticEvent<HTMLFormElement>) => {
        event.preventDefault(); // Prevent the default form submission

        axios({
            method: "POST",
            url: "/token",
            data: {
                email: loginForm.email,
                password: loginForm.password
            }
        })
        .then((response) => {
            props.setToken(response.data.access_token);
        }).catch((error) => {
            if (error.response) {
                console.log(error.response)
                console.log(error.response.status)
                console.log(error.response.headers)
            }
        });

        // Optionally reset the form fields
        setloginForm(({
            email: "",
            password: ""}))
    };

    return(
            <Form onSubmit={logMeIn}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label><h5>Email Address</h5></Form.Label>
                    <Form.Control type="email" placeholder="Enter email" />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label><h5>Password</h5></Form.Label>
                    <Form.Control type="password" placeholder="Enter password" />
                </Form.Group>
                <Link to={props.urlText}>
                    <Button variant="primary" type="submit" className="rounded-pill login-button" size="sm">
                        <p>{props.buttonText}</p>
                    </Button>
                </Link>
                <br></br>
                <br></br>
                <Link to="/RegisterScreen" id="link-text"><p>{props.registerText}</p></Link>
            </Form>
    );
}

export default LoginForm;
